import { useEffect, useState } from "react";
import axios from "axios";

export default function Sessions() {
  const [sessions, setSessions] = useState([]);
  const [focusScore, setFocusScore] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const res = await axios.get("http://localhost:5001/api/sessions", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSessions(res.data);
      } catch (err) {
        console.error(err.response?.data || err.message);
      }
    };
    fetchSessions();
  }, [token]);

  const createSession = async () => {
    try {
      const res = await axios.post(
        "http://localhost:5001/api/sessions",
        { focusScore: Number(focusScore) },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSessions([res.data, ...sessions]);
      setFocusScore("");
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to create session");
    }
  };

  return (
    <div>
      <h1>Sessions</h1>
      <input
        type="number"
        placeholder="Focus Score (0-10)"
        value={focusScore}
        onChange={(e) => setFocusScore(e.target.value)}
      />
      <button onClick={createSession}>Start Session</button>

      <h2>History</h2>
      <ul>
        {sessions.map((s) => (
          <li key={s._id}>
            Type: {s.sessionType}, Duration: {s.duration} min, Score: {s.focusScore}
          </li>
        ))}
      </ul>
    </div>
  );
}
