import { useEffect, useState } from "react";
import axios from "axios";
import Timer from "../components/Timer";

export default function Sessions() {
  const [sessions, setSessions] = useState([]);
  const [focusScore, setFocusScore] = useState("");
  const [showTimer, setShowTimer] = useState(false);
  const [duration, setDuration] = useState(25); // adaptive duration

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const res = await axios.get("http://localhost:5001/api/sessions", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSessions(res.data);
      } catch (err) {
        console.error(err.response?.data || err.message);
      }
    };
    fetchSessions();
  }, [token]);

  // Adaptive duration logic based on focusScore
  const getAdaptiveDuration = (score) => {
    if (score >= 10) return 120;
    if (score >= 7) return 90;
    if (score >= 5) return 60;
    if (score >= 3) return 30;
    return 15;
  };

  const startSession = () => {
    const score = Number(focusScore);
    if (isNaN(score) || score < 0 || score > 10) {
      alert("Enter a valid score between 0 and 10");
      return;
    }
    setDuration(getAdaptiveDuration(score));
    setShowTimer(true);
  };

  const handleSessionComplete = async () => {
    alert("ðŸŽ‰ Session complete! Saving your progress...");

    try {
      const res = await axios.post(
        "http://localhost:5001/api/sessions",
        { focusScore: Number(focusScore), duration },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSessions([res.data, ...sessions]); // history update
      setFocusScore("");
      setDuration(25);
      setShowTimer(false);
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to save session");
    }
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Sessions</h1>

      {/* Focus Score Input */}
      {!showTimer && (
        <div className="mb-4">
          <input
            type="number"
            placeholder="Focus Score (0-10)"
            value={focusScore}
            onChange={(e) => setFocusScore(e.target.value)}
            className="border px-2 py-1 rounded mr-2"
          />
          <button
            onClick={startSession}
            className="bg-green-500 text-white px-4 py-2 rounded"
          >
            Start Adaptive Session
          </button>
        </div>
      )}

      {/* Timer Section */}
      {showTimer && <Timer minutes={duration} onComplete={handleSessionComplete} />}

      {/* History */}
      <h2 className="text-xl font-semibold mt-6">History</h2>
      <ul className="list-disc pl-6">
        {sessions.map((s) => (
          <li key={s._id}>
            Type: {s.sessionType}, Duration: {s.duration} min, Score: {s.focusScore}
          </li>
        ))}
      </ul>
    </div>
  );
}
