import { useEffect, useState } from "react";
import axios from "axios";
import Timer from "../components/Timer";

export default function Sessions() {
  const [sessions, setSessions] = useState([]);
  const [focusScore, setFocusScore] = useState("");
  const [showTimer, setShowTimer] = useState(false);
  const [duration, setDuration] = useState(25); // default 25 min

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const res = await axios.get("http://localhost:5001/api/sessions", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setSessions(res.data);
      } catch (err) {
        console.error(err.response?.data || err.message);
      }
    };
    fetchSessions();
  }, [token]);

  const createSession = async () => {
    try {
      const res = await axios.post(
        "http://localhost:5001/api/sessions",
        { focusScore: Number(focusScore), duration },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSessions([res.data, ...sessions]);
      setFocusScore("");
    } catch (err) {
      console.error(err.response?.data || err.message);
      alert("Failed to create session");
    }
  };

  const handleSessionComplete = () => {
    alert("ðŸŽ‰ Session complete! Time to log your focus score.");
    setShowTimer(false);
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Sessions</h1>

      {/* Duration Selector */}
      <div className="mb-4">
        <label className="mr-2">Choose Duration: </label>
        <select
          value={duration}
          onChange={(e) => setDuration(Number(e.target.value))}
          className="border px-2 py-1 rounded"
        >
          <option value={10}>10 min</option>
          <option value={25}>25 min</option>
          <option value={30}>30 min</option>
        </select>
      </div>

      {/* Timer Section */}
      {showTimer ? (
        <Timer minutes={duration} onComplete={handleSessionComplete} />
      ) : (
        <button
          onClick={() => setShowTimer(true)}
          className="bg-green-500 text-white px-4 py-2 rounded mb-4"
        >
          Start {duration}-Min Timer
        </button>
      )}

      {/* Focus Score Input */}
      <div className="mt-4">
        <input
          type="number"
          placeholder="Focus Score (0-10)"
          value={focusScore}
          onChange={(e) => setFocusScore(e.target.value)}
          className="border px-2 py-1 rounded mr-2"
        />
        <button
          onClick={createSession}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Save Session
        </button>
      </div>

      {/* History */}
      <h2 className="text-xl font-semibold mt-6">History</h2>
      <ul className="list-disc pl-6">
        {sessions.map((s) => (
          <li key={s._id}>
            Type: {s.sessionType}, Duration: {s.duration} min, Score:{" "}
            {s.focusScore}
          </li>
        ))}
      </ul>
    </div>
  );
}
