import { useEffect, useState } from "react";
import { initializeApp } from "firebase/app";
import { getAuth, signInWithCustomToken, signInAnonymously, signOut, onAuthStateChanged } from "firebase/auth";
import { getFirestore, collection, query, orderBy, onSnapshot, addDoc } from "firebase/firestore";
import { Transition, CSSTransition } from 'react-transition-group';

// --- Firebase Configuration and Initialization ---
// NOTE: These variables are provided by the canvas environment.
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// --- Helper Components & Data ---

// Timer Component
const Timer = ({ minutes, onComplete, onCancel }) => {
  const [time, setTime] = useState(minutes * 60);

  useEffect(() => {
    if (time <= 0) {
      onComplete();
      return;
    }

    const intervalId = setInterval(() => {
      setTime(prevTime => prevTime - 1);
    }, 1000);

    return () => clearInterval(intervalId);
  }, [time, onComplete]);

  const formatTime = (seconds) => {
    const min = Math.floor(seconds / 60);
    const sec = seconds % 60;
    return `${String(min).padStart(2, '0')}:${String(sec).padStart(2, '0')}`;
  };

  return (
    <div className="flex flex-col items-center justify-center p-8 bg-white dark:bg-gray-800 rounded-2xl shadow-xl transition-all duration-300">
      <div className="text-8xl font-light text-indigo-600 dark:text-indigo-400 font-mono tracking-wide animate-pulse">
        {formatTime(time)}
      </div>
      <p className="mt-2 text-gray-500 dark:text-gray-400 text-sm">Time Remaining</p>
      <button
        onClick={onCancel}
        className="mt-6 px-6 py-3 bg-red-500 hover:bg-red-600 text-white font-semibold rounded-full shadow-lg transition-all duration-300 transform hover:scale-105"
      >
        Cancel Session
      </button>
    </div>
  );
};


// Biometric Mood Detection Component
const BiometricMoodDetection = () => {
  const [heartRate, setHeartRate] = useState(null);
  const [stressLevel, setStressLevel] = useState('low');

  useEffect(() => {
    const simulateBiometrics = () => {
      const hr = Math.floor(Math.random() * 30) + 60; // 60-90 BPM
      setHeartRate(hr);

      if (hr > 80) setStressLevel('high');
      else if (hr > 70) setStressLevel('medium');
      else setStressLevel('low');
    };

    const interval = setInterval(simulateBiometrics, 5000);
    simulateBiometrics(); // Initial call
    return () => clearInterval(interval);
  }, []);

  const stressColor = {
    low: 'text-green-500',
    medium: 'text-yellow-500',
    high: 'text-red-500',
  };

  return (
    <div className="flex flex-col md:flex-row items-center p-6 bg-white dark:bg-gray-800 rounded-2xl shadow-lg transition-all duration-300 animate-fade-in-up">
      <div className="flex-shrink-0 mr-4 text-3xl text-red-500 animate-pulse">
        🫀
      </div>
      <div className="flex-1">
        <h3 className="text-xl font-bold text-gray-800 dark:text-gray-200">Real-time Mood Detection</h3>
        <div className="mt-2 flex items-center space-x-4">
          <div className="text-sm text-gray-600 dark:text-gray-400">
            Heart Rate: <span className="font-semibold">{heartRate || '--'} BPM</span>
          </div>
          <div className="flex items-center space-x-1">
            <span className="text-sm text-gray-600 dark:text-gray-400">Stress:</span>
            <span className={`font-semibold capitalize ${stressColor[stressLevel]}`}>
              {stressLevel}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

// SVG for Trees
const TreeSVG = ({ type, health, position }) => {
  const color = `hsl(120, ${Math.min(health, 100)}%, 30%)`;
  const size = type === 'oak' ? 24 : type === 'pine' ? 20 : 12;

  // Simple SVG tree shapes
  const treeShapes = {
    sapling: (
      <path
        d="M12 24 L10 12 L14 12 L12 24 Z M12 12 C16 8, 16 0, 12 0 C8 0, 8 8, 12 12 Z"
        fill={color}
        stroke="#4B5563"
        strokeWidth="0.5"
      />
    ),
    pine: (
      <path
        d="M12 24 L10 18 L14 18 L12 24 Z M12 18 L5 12 L19 12 L12 18 Z M12 12 L0 6 L24 6 L12 12 Z"
        fill={color}
        stroke="#4B5563"
        strokeWidth="0.5"
      />
    ),
    oak: (
      <path
        d="M12 24 L10 16 L14 16 L12 24 Z M12 16 C20 12, 20 4, 12 4 C4 4, 4 12, 12 16 Z M12 16 C16 12, 16 8, 12 8 C8 8, 8 12, 12 16 Z"
        fill={color}
        stroke="#4B5563"
        strokeWidth="0.5"
      />
    ),
  };

  return (
    <svg
      x={position.x}
      y={position.y}
      width={size * 1.5}
      height={size * 1.5}
      viewBox="0 0 24 24"
      className="transition-transform duration-500 ease-in-out transform hover:scale-125"
    >
      {treeShapes[type]}
    </svg>
  );
};


// Virtual Forest Component
const VirtualForest = ({ sessions }) => {
  const [trees, setTrees] = useState([]);
  const [forestLevel, setForestLevel] = useState(1);

  useEffect(() => {
    const newTrees = sessions.slice(0, 25).map((session, idx) => ({
      id: idx,
      type: session.duration > 60 ? 'oak' : session.duration > 30 ? 'pine' : 'sapling',
      health: Math.min((session.focusScore / 10) * 100, 100),
      position: { 
        x: 20 + (idx % 5) * 60, 
        y: 20 + Math.floor(idx / 5) * 60 
      }
    }));
    setTrees(newTrees);
    setForestLevel(Math.floor(sessions.length / 10) + 1);
  }, [sessions]);

  const avgHealth = Math.round(trees.reduce((acc, t) => acc + t.health, 0) / trees.length || 0);

  return (
    <div className="p-6 bg-white dark:bg-gray-800 rounded-2xl shadow-xl transition-all duration-300 animate-fade-in-up">
      <h3 className="text-2xl font-bold text-gray-800 dark:text-gray-200 flex items-center space-x-2">
        <span className="text-green-600">🌳</span>
        <span>Your Focus Forest</span>
        <span className="bg-green-100 text-green-800 text-sm font-semibold px-2.5 py-0.5 rounded-full dark:bg-green-900 dark:text-green-300">
          Level {forestLevel}
        </span>
      </h3>
      <div className="relative mt-4 bg-gray-100 dark:bg-gray-900 rounded-lg p-4 h-72 overflow-hidden">
        <svg width="100%" height="100%" className="forest-svg">
          {trees.map(tree => (
            <TreeSVG 
              key={tree.id} 
              type={tree.type} 
              health={tree.health} 
              position={tree.position} 
            />
          ))}
        </svg>
      </div>
      <div className="mt-4 flex justify-around text-center text-gray-600 dark:text-gray-400">
        <div>
          <span className="block font-semibold text-lg">{trees.length}</span>
          <span className="text-sm">Trees Planted</span>
        </div>
        <div>
          <span className="block font-semibold text-lg">{avgHealth}%</span>
          <span className="text-sm">Average Health</span>
        </div>
        <div>
          <span className="block font-semibold text-lg">{forestLevel}</span>
          <span className="text-sm">Forest Level</span>
        </div>
      </div>
    </div>
  );
};


// Focus Challenges Component
const FocusChallenges = ({ sessions }) => {
  const [challenges, setChallenges] = useState([]);

  useEffect(() => {
    const weeklySessionCount = sessions.filter(s => 
      new Date(s.createdAt) > new Date(Date.now() - 7 * 24 * 60 * 60 * 1000)
    ).length;

    const longestSession = sessions.length > 0 ? Math.max(...sessions.map(s => s.duration), 0) : 0;
    const consecutiveDays = calculateConsecutiveDays(sessions);

    setChallenges([
      { 
        id: 1, 
        title: "Week Warrior", 
        description: "Complete 7 sessions this week", 
        progress: weeklySessionCount, 
        target: 7, 
        reward: "🏅 Gold Badge" 
      },
      { 
        id: 2, 
        title: "Deep Diver", 
        description: "Complete a 2-hour session", 
        progress: longestSession >= 120 ? 1 : 0, 
        target: 1, 
        reward: "🏊‍♂️ Deep Focus Badge" 
      },
      { 
        id: 3, 
        title: "Consistency King", 
        description: "Study 5 days in a row", 
        progress: consecutiveDays, 
        target: 5, 
        reward: "👑 Consistency Crown" 
      }
    ]);
  }, [sessions]);

  const calculateConsecutiveDays = (sessions) => {
    if (sessions.length === 0) return 0;

    const sortedDates = sessions
      .map(s => new Date(s.createdAt).toLocaleDateString())
      .filter((v, i, a) => a.indexOf(v) === i) // unique dates
      .sort((a, b) => new Date(b) - new Date(a)); // sort descending

    let count = 0;
    let prevDate = new Date();

    for (const dateStr of sortedDates) {
      const currentDate = new Date(dateStr);
      if (count === 0) {
        // First day check
        const diff = Math.floor((prevDate - currentDate) / (1000 * 60 * 60 * 24));
        if (diff <= 1) {
          count = 1;
        } else {
          return count;
        }
      } else {
        const diff = Math.floor((prevDate - currentDate) / (1000 * 60 * 60 * 24));
        if (diff === 1) {
          count++;
        } else if (diff > 1) {
          break; // Streak broken
        }
      }
      prevDate = currentDate;
    }
    return count;
  };

  return (
    <div className="p-6 bg-white dark:bg-gray-800 rounded-2xl shadow-xl transition-all duration-300 animate-fade-in-up">
      <h3 className="text-2xl font-bold text-gray-800 dark:text-gray-200 flex items-center space-x-2">
        <span className="text-yellow-500">🏆</span>
        <span>Focus Challenges</span>
      </h3>
      <div className="mt-4 grid grid-cols-1 md:grid-cols-3 gap-4">
        {challenges.map(challenge => (
          <div key={challenge.id} className="relative p-6 bg-gray-100 dark:bg-gray-900 rounded-xl shadow-inner overflow-hidden">
            <h4 className="text-lg font-bold text-gray-800 dark:text-gray-200">{challenge.title}</h4>
            <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">{challenge.description}</p>
            <div className="mt-4 h-2 bg-gray-200 rounded-full overflow-hidden">
              <div
                className="h-full bg-gradient-to-r from-blue-400 to-indigo-500 transition-all duration-500 ease-in-out"
                style={{ width: `${Math.min((challenge.progress / challenge.target) * 100, 100)}%` }}
              ></div>
            </div>
            <div className="mt-2 flex justify-between text-sm text-gray-600 dark:text-gray-400 font-medium">
              <span>Progress: {challenge.progress}/{challenge.target}</span>
              <span className="text-indigo-500">{challenge.reward}</span>
            </div>
            {challenge.progress >= challenge.target && (
              <div className="absolute inset-0 bg-white/50 dark:bg-black/50 flex items-center justify-center backdrop-blur-sm">
                <span className="text-4xl animate-bounce">🎉</span>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};


// Ambient Sounds Component
const AmbientSounds = () => {
  const [currentSound, setCurrentSound] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  // SVG icons for soundscapes
  const soundIcons = {
    rain: (
      <svg xmlns="http://www.w3.org/2000/svg" className="h-10 w-10 text-blue-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21H6.536a2 2 0 01-1.789-2.894l3.5-7A2 2 0 019.263 15h4.764z" />
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 11V9M10 5l4-4" />
      </svg>
    ),
    forest: (
      <svg xmlns="http://www.w3.org/2000/svg" className="h-10 w-10 text-green-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.899A2.182 2.182 0 0111.828 21a2.182 2.182 0 01-1.586-.599L6.343 16.657m11.314-11.314L13.414 3.101A2.182 2.182 0 0011.828 3a2.182 2.182 0 00-1.586.599L6.343 5.343m11.314 0c.75-3.321 2.9-6.321 4.54-7.586a1.996 1.996 0 00-2.828-2.828c-1.265 1.64-4.265 3.79-7.586 4.54m-11.314 0c-.75-3.321-2.9-6.321-4.54-7.586a1.996 1.996 0 012.828-2.828c1.64 1.265 3.79 3.321 4.54 7.586m0 0c1.265 1.64 3.321 3.79 7.586 4.54M17.657 16.657c-1.265 1.64-3.321 3.79-7.586 4.54" />
      </svg>
    ),
    ocean: (
      <svg xmlns="http://www.w3.org/2000/svg" className="h-10 w-10 text-blue-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5a2 2 0 01-2-2V7a2 2 0 012-2h14a2 2 0 012 2v2a2 2 0 01-2 2zM19 11v6a2 2 0 01-2 2H7a2 2 0 01-2-2v-6m14-4a2 2 0 00-2-2H7a2 2 0 00-2 2M15 17h-2" />
      </svg>
    ),
    cafe: (
      <svg xmlns="http://www.w3.org/2000/svg" className="h-10 w-10 text-yellow-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4.75L12 17.25M12 4.75L15.366 8.5M12 4.75L8.634 8.5M12 17.25L15.366 13.5M12 17.25L8.634 13.5M12 17.25a6 6 0 100 0z" />
      </svg>
    ),
    'lo-fi': (
      <svg xmlns="http://www.w3.org/2000/svg" className="h-10 w-10 text-purple-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0h2a2 2 0 002-2v-6a2 2 0 00-2-2H9m0 0V9a2 2 0 012-2h2a2 2 0 012 2v2.5M14 17h6" />
      </svg>
    ),
  };

  const soundscapes = [
    { id: 'rain', name: 'Rain', description: 'Gentle rainfall' },
    { id: 'forest', name: 'Forest', description: 'Birds & rustling leaves' },
    { id: 'ocean', name: 'Ocean Waves', description: 'Calming ocean sounds' },
    { id: 'cafe', name: 'Coffee Shop', description: 'Background café chatter' },
    { id: 'lo-fi', name: 'Lo-Fi Beats', description: 'Chill study music' }
  ];

  const playSound = (soundId) => {
    if (currentSound === soundId && isPlaying) {
      setIsPlaying(false);
      setCurrentSound(null);
    } else {
      setCurrentSound(soundId);
      setIsPlaying(true);
    }
  };

  return (
    <div className="p-6 bg-white dark:bg-gray-800 rounded-2xl shadow-xl transition-all duration-300 animate-fade-in-up">
      <h3 className="text-2xl font-bold text-gray-800 dark:text-gray-200 flex items-center space-x-2">
        <span className="text-pink-500">🎧</span>
        <span>Focus Soundscapes</span>
      </h3>
      <div className="mt-4 grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
        {soundscapes.map(sound => (
          <button
            key={sound.id}
            onClick={() => playSound(sound.id)}
            className={`flex flex-col items-center justify-center p-4 rounded-xl shadow-lg transition-all duration-300 ease-in-out transform hover:scale-105 
              ${currentSound === sound.id && isPlaying ? 'bg-indigo-100 dark:bg-indigo-900 ring-4 ring-indigo-500' : 'bg-gray-100 dark:bg-gray-900'}
            `}
          >
            {soundIcons[sound.id]}
            <h4 className="mt-2 text-md font-semibold text-gray-800 dark:text-gray-200">{sound.name}</h4>
            <p className="mt-1 text-xs text-gray-500 dark:text-gray-400">{sound.description}</p>
          </button>
        ))}
      </div>
    </div>
  );
};


// Main Sessions Component
export default function App() {
  const [sessions, setSessions] = useState([]);
  const [focusScore, setFocusScore] = useState("");
  const [showTimer, setShowTimer] = useState(false);
  const [duration, setDuration] = useState(25);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('session');
  const [appInitialized, setAppInitialized] = useState(false);
  const [userId, setUserId] = useState(null);

  // --- Firebase setup and authentication ---
  useEffect(() => {
    const app = initializeApp(firebaseConfig);
    const db = getFirestore(app);
    const auth = getAuth(app);

    const setupApp = async () => {
      try {
        if (initialAuthToken) {
          await signInWithCustomToken(auth, initialAuthToken);
        } else {
          await signInAnonymously(auth);
        }
      } catch (error) {
        console.error("Firebase Auth Error:", error);
      }
    };

    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setUserId(user.uid);
        // Set up real-time listener for sessions
        const sessionCollectionPath = `/artifacts/${appId}/users/${user.uid}/sessions`;
        const q = query(collection(db, sessionCollectionPath), orderBy("createdAt", "desc"));

        const unsubscribeSnapshot = onSnapshot(q, (snapshot) => {
          const fetchedSessions = snapshot.docs.map(doc => ({
            id: doc.id,
            ...doc.data(),
            createdAt: doc.data().createdAt?.toDate().toISOString() // Convert Timestamp to string
          }));
          setSessions(fetchedSessions);
          setLoading(false);
        }, (error) => {
          console.error("Firestore Error:", error);
          setLoading(false);
        });

        // Cleanup listener on unmount
        return () => unsubscribeSnapshot();
      } else {
        setUserId(null);
        setSessions([]);
        setLoading(false);
      }
      setAppInitialized(true);
    });

    if (!appInitialized) {
      setupApp();
    }

    return () => unsubscribe();
  }, [appInitialized, initialAuthToken]);


  // Adaptive duration logic based on focusScore
  const getAdaptiveDuration = (score) => {
    if (score >= 9) return 120;
    if (score >= 7) return 90;
    if (score >= 5) return 60;
    if (score >= 3) return 30;
    return 15;
  };

  // Get session type description
  const getSessionDescription = (score) => {
    if (score >= 9) return "Deep Focus Marathon";
    if (score >= 7) return "Extended Focus Session";
    if (score >= 5) return "Standard Focus Session";
    if (score >= 3) return "Quick Focus Boost";
    return "Micro Focus Session";
  };

  // Start session with adaptive timer
  const startSession = () => {
    const score = Number(focusScore);
    if (isNaN(score) || score < 0 || score > 10) {
      // Replaced alert with a simple text-based error message
      // A more complex modal UI would be better in a full app
      return;
    }
    const adaptiveDuration = getAdaptiveDuration(score);
    setDuration(adaptiveDuration);
    setShowTimer(true);
    setActiveTab('timer');
  };

  // When timer completes, save session automatically
  const handleSessionComplete = async () => {
    setLoading(true);
    try {
      const db = getFirestore(initializeApp(firebaseConfig));
      const auth = getAuth();
      const user = auth.currentUser;

      if (!user) {
        throw new Error("User not authenticated.");
      }

      const sessionsCollectionRef = collection(db, `/artifacts/${appId}/users/${user.uid}/sessions`);

      await addDoc(sessionsCollectionRef, {
        focusScore: Number(focusScore),
        duration: duration,
        createdAt: new Date(),
      });
      setFocusScore("");
      setShowTimer(false);
      setActiveTab('session');
    } catch (err) {
      console.error("Failed to save session:", err);
    } finally {
      setLoading(false);
    }
  };

  // Handle timer cancel/stop
  const handleTimerCancel = () => {
    setShowTimer(false);
    setFocusScore("");
    setActiveTab('session');
  };

  const handleLogout = async () => {
    const auth = getAuth(initializeApp(firebaseConfig));
    try {
      await signOut(auth);
    } catch (error) {
      console.error("Logout failed:", error);
    }
    window.location.href = "/login";
  };

  const getSessionType = (score) => {
    if (score >= 9) return 'marathon';
    if (score >= 7) return 'extended';
    if (score >= 5) return 'standard';
    return 'quick';
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors duration-500 font-sans antialiased text-gray-900 dark:text-gray-100 flex flex-col items-center py-10 px-4 md:px-8">
      {/* Header */}
      <header className="w-full max-w-5xl mb-10 flex flex-col md:flex-row items-center justify-between space-y-4 md:space-y-0">
        <div className="flex flex-col text-center md:text-left">
          <h1 className="text-4xl md:text-5xl font-extrabold text-indigo-600 dark:text-indigo-400">StudyMood</h1>
          <p className="text-xl md:text-2xl font-light mt-1">Adaptive Focus Sessions</p>
        </div>
        <div className="flex items-center space-x-4">
          <span className="text-sm text-gray-500 dark:text-gray-400 font-mono hidden md:block">User ID: <span className="font-bold">{userId || 'Loading...'}</span></span>
          <button onClick={handleLogout} className="flex items-center space-x-2 px-4 py-2 bg-white dark:bg-gray-800 text-gray-600 dark:text-gray-300 rounded-full shadow-lg transition-all duration-300 transform hover:scale-105 hover:shadow-xl">
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1m0-5v-1m0 0a2 2 0 01-2-2V7a2 2 0 012-2h4a2 2 0 012 2v2" />
            </svg>
            <span className="font-medium">Logout</span>
          </button>
        </div>
      </header>

      {/* Main Content Area */}
      <div className="w-full max-w-5xl space-y-8">

        {/* Navigation Tabs */}
        <div className="flex flex-wrap justify-center space-x-2 md:space-x-4 p-2 bg-gray-200 dark:bg-gray-800 rounded-full shadow-inner">
          <button 
            className={`tab px-4 py-2 rounded-full font-semibold transition-all duration-300 transform hover:scale-105
              ${activeTab === 'session' ? 'bg-indigo-500 text-white shadow-md' : 'text-gray-600 dark:text-gray-400 hover:bg-white hover:text-indigo-500 dark:hover:bg-gray-900'}
            `}
            onClick={() => setActiveTab('session')}
          >
            🎯 Focus Session
          </button>
          <button 
            className={`tab px-4 py-2 rounded-full font-semibold transition-all duration-300 transform hover:scale-105
              ${activeTab === 'forest' ? 'bg-indigo-500 text-white shadow-md' : 'text-gray-600 dark:text-gray-400 hover:bg-white hover:text-indigo-500 dark:hover:bg-gray-900'}
            `}
            onClick={() => setActiveTab('forest')}
          >
            🌳 My Forest
          </button>
          <button 
            className={`tab px-4 py-2 rounded-full font-semibold transition-all duration-300 transform hover:scale-105
              ${activeTab === 'sounds' ? 'bg-indigo-500 text-white shadow-md' : 'text-gray-600 dark:text-gray-400 hover:bg-white hover:text-indigo-500 dark:hover:bg-gray-900'}
            `}
            onClick={() => setActiveTab('sounds')}
          >
            🎧 Sounds
          </button>
          <button 
            className={`tab px-4 py-2 rounded-full font-semibold transition-all duration-300 transform hover:scale-105
              ${activeTab === 'challenges' ? 'bg-indigo-500 text-white shadow-md' : 'text-gray-600 dark:text-gray-400 hover:bg-white hover:text-indigo-500 dark:hover:bg-gray-900'}
            `}
            onClick={() => setActiveTab('challenges')}
          >
            🏆 Challenges
          </button>
        </div>

        {/* Biometric Panel - Always visible */}
        <BiometricMoodDetection />

        {/* Tab Content */}
        <div className="w-full">
          <CSSTransition
            in={activeTab === 'session' && !showTimer}
            timeout={300}
            classNames="fade"
            unmountOnExit
          >
            <div>
              {/* Session Starter */}
              <div className="p-8 bg-white dark:bg-gray-800 rounded-2xl shadow-xl transition-all duration-300 animate-fade-in-up">
                <div className="text-center mb-6">
                  <h2 className="text-3xl font-bold text-gray-800 dark:text-gray-200">Start New Session</h2>
                  <p className="mt-2 text-gray-500 dark:text-gray-400">Rate your current focus level to get a personalized session</p>
                </div>

                <div className="flex flex-col items-center space-y-6">
                  <div className="w-full max-w-xs">
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300">
                      Current Focus Level (0-10)
                    </label>
                    <div className="mt-1 flex items-center space-x-2 rounded-full p-1 bg-gray-100 dark:bg-gray-700">
                      <input
                        type="range"
                        min="0"
                        max="10"
                        step="1"
                        value={focusScore}
                        onChange={(e) => setFocusScore(e.target.value)}
                        className="flex-1 h-2 bg-gradient-to-r from-red-400 via-yellow-400 to-green-500 rounded-full appearance-none cursor-pointer"
                      />
                      <span className="text-lg font-bold text-gray-800 dark:text-gray-200 w-10 text-center">{focusScore}</span>
                    </div>
                  </div>

                  {focusScore && (
                    <div className="p-4 bg-gray-100 dark:bg-gray-900 rounded-xl w-full max-w-lg shadow-inner animate-slide-in">
                      <div className="flex justify-between items-center text-sm">
                        <span className="font-semibold text-gray-600 dark:text-gray-300">Session Type:</span>
                        <span className="text-indigo-600 dark:text-indigo-400 font-bold">{getSessionDescription(Number(focusScore))}</span>
                      </div>
                      <div className="flex justify-between items-center mt-2 text-sm">
                        <span className="font-semibold text-gray-600 dark:text-gray-300">Duration:</span>
                        <span className="text-indigo-600 dark:text-indigo-400 font-bold">{getAdaptiveDuration(Number(focusScore))} minutes</span>
                      </div>
                    </div>
                  )}

                  <button
                    onClick={startSession}
                    className="w-full max-w-sm px-6 py-3 bg-gradient-to-r from-indigo-500 to-blue-600 text-white font-bold rounded-full shadow-lg transition-all duration-300 transform hover:scale-105 hover:shadow-2xl"
                    disabled={!focusScore || loading}
                  >
                    {loading ? 'Preparing...' : 'Begin Focus Session'}
                  </button>
                </div>
              </div>

              {/* Session History */}
              <div className="mt-8 p-8 bg-white dark:bg-gray-800 rounded-2xl shadow-xl transition-all duration-300 animate-fade-in-up">
                <div className="flex items-center justify-between mb-6">
                  <h2 className="text-2xl font-bold text-gray-800 dark:text-gray-200">Session History</h2>
                  {sessions.length > 0 && (
                    <span className="text-sm font-semibold text-gray-500 dark:text-gray-400">{sessions.length} sessions completed</span>
                  )}
                </div>

                {loading ? (
                  <div className="flex flex-col items-center justify-center py-10">
                    <svg className="animate-spin h-10 w-10 text-indigo-500" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    <p className="mt-4 text-gray-500 dark:text-gray-400">Loading sessions...</p>
                  </div>
                ) : sessions.length === 0 ? (
                  <div className="text-center py-10">
                    <div className="text-6xl mb-4">🎯</div>
                    <h3 className="text-xl font-semibold text-gray-700 dark:text-gray-300">No sessions yet</h3>
                    <p className="mt-2 text-gray-500 dark:text-gray-400">Start your first focus session to begin tracking your progress!</p>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {sessions.map((session, index) => (
                      <div key={session.id} className="p-5 bg-gray-100 dark:bg-gray-900 rounded-xl shadow-md transition-all duration-300 transform hover:scale-105">
                        <div className="flex items-center justify-between text-sm text-gray-500 dark:text-gray-400">
                          <span>Session #{sessions.length - index}</span>
                          <span className={`px-2 py-0.5 rounded-full text-xs font-semibold uppercase ${
                            getSessionType(session.focusScore) === 'marathon' ? 'bg-purple-100 text-purple-800' :
                            getSessionType(session.focusScore) === 'extended' ? 'bg-indigo-100 text-indigo-800' :
                            getSessionType(session.focusScore) === 'standard' ? 'bg-blue-100 text-blue-800' :
                            'bg-gray-200 text-gray-800'
                          }`}>
                            {getSessionDescription(session.focusScore).split(' ')[0]}
                          </span>
                        </div>
                        <div className="mt-3 text-lg font-bold text-gray-800 dark:text-gray-200">{session.duration} minutes</div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">Focus Score: <span className="font-semibold">{session.focusScore} / 10</span></div>
                        <div className="mt-3 text-xs text-gray-500 dark:text-gray-400 border-t border-gray-200 dark:border-gray-700 pt-2">
                          Completed on: {new Date(session.createdAt).toLocaleDateString('en-US', {
                            month: 'short', day: 'numeric', year: 'numeric'
                          })}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </CSSTransition>

          {/* Timer Section */}
          <CSSTransition
            in={activeTab === 'timer' && showTimer}
            timeout={300}
            classNames="fade"
            unmountOnExit
          >
            <div className="flex items-center justify-center p-8">
              <Timer 
                minutes={duration} 
                onComplete={handleSessionComplete}
                onCancel={handleTimerCancel}
              />
            </div>
          </CSSTransition>

          {/* Other Tabs */}
          <CSSTransition in={activeTab === 'forest'} timeout={300} classNames="fade" unmountOnExit>
            <VirtualForest sessions={sessions} />
          </CSSTransition>
          <CSSTransition in={activeTab === 'sounds'} timeout={300} classNames="fade" unmountOnExit>
            <AmbientSounds />
          </CSSTransition>
          <CSSTransition in={activeTab === 'challenges'} timeout={300} classNames="fade" unmountOnExit>
            <FocusChallenges sessions={sessions} />
          </CSSTransition>
        </div>
      </div>
    </div>
  );
}
